import React, { forwardRef } from 'react';
import { Platform } from 'react-native';
export const SelectSectionHeaderText = (StyledSelectSectionHeaderText) => forwardRef(({ children, ...props }, ref) => {
    if (Platform.OS === 'web') {
        return <></>;
    }
    return (<StyledSelectSectionHeaderText {...props} ref={ref}>
        {children}
      </StyledSelectSectionHeaderText>);
});
