import React, { memo } from 'react';
import { forwardRef } from 'react';
import { TabProvider } from './TabProvider';
export const Tabs = (StyledTabs) => memo(forwardRef(({ value, ...props }, ref) => {
    const DEFAULT_TAB = 'tab-0';
    const [currentActiveTab, setCurrentActiveTab] = React.useState(value ?? DEFAULT_TAB);
    const onChange = (currentValue) => setCurrentActiveTab(currentValue);
    return (<TabProvider currentActiveTab={currentActiveTab} onChange={onChange}>
          <StyledTabs {...props} ref={ref}/>
        </TabProvider>);
}));
